import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";
import Sketch from 'react-p5';
import styled from "styled-components";
import Chat from "./Chat";
import RoomSetup from './RoomSetup';

const socket = io.connect("http://localhost:3001");

const StyledVideo = styled.video`
	height: 300px;
	width: 300px;
`;

const Video = (props) => {
	const ref = useRef();

	useEffect(() => {
		ref.current.srcObject = props.peer;
	}, []);

	return (
			props.muted ? (
				<StyledVideo muted playsInline autoPlay ref={ ref } />
			) : (
				<StyledVideo playsInline autoPlay ref={ ref } />
			)
	);
}

function Room(props) {
		const [name, setName] = useState("");
		const [mic, setMic] = useState(true);
		const [cam, setCam] = useState(true);
		const [joinedRoom, setJoinedRoom] = useState(false);

		const [users, setUsers] = useState([]);
		const [peers, setPeers] = useState([]);
		const [myVideo, setMyVideo] = useState({});
		const [connected, setConnected] = useState({});
		const peersRef = useRef([]);
		var currentPeer = [];
    const roomID = props.match.params.roomID;

		useEffect(() => {
			socket.emit("join room", roomID);
			navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
				setMyVideo(stream).then(() => {
					if (!cam) {
						cameraOnOff();
					}
					if (!mic) {
						muteUnmute();
					}
				});

				socket.on("all users", users => {
						const peers = [];
						users.forEach(userID => {
								const peer = createPeer(userID, socket.id, stream);
								peersRef.current.push({
										peerID: userID,
										peer,
								})
								peers.push(peer);
						})
						setPeers(peers);
				})

				socket.on("user joined", payload => {
						const peer = addPeer(payload.signal, payload.callerID, stream);
						peersRef.current.push({
								peerID: payload.callerID,
								peer,
						})

						setPeers(users => [...users, peer]);
				});

				socket.on("receiving returned signal", payload => {
						const item = peersRef.current.find(p => p.peerID === payload.id);
						item.peer.signal(payload.signal);
				});
		})
		}, []);

	useEffect(() => {
		socket.on('receive move', (data) => {
			setUsers(data.all);
			for (let i = 0; i < data.all.length; i ++) {
				if (data.all[i].id === data.me.id) {
					continue;
				}

				let closer = proximity(data.all[i], data.me)

				if (closer && connected[data.all[i].id] === undefined) {
					connectPeer(data.all[i], data.me);

				} else if (!closer && connected[data.all[i].id] !== undefined) {
					disconnectPeer(data.all[i], data.me);
				}
			}
		})
	}, [socket]);

	const createPeer = (userToSignal, callerID, stream) => {
		const peer = new Peer({
			initiator: true,
			trickle: false,
			stream
		})

		peer.on('signal', signal => {
			socket.emit('sending signal', { userToSignal, callerID, signal })
		})

		return peer;
	}

	const addPeer = (incomingSignal, callerID, stream) => {
		const peer = new Peer({
			initiator: false, 
			trickle: false,
			stream
		})

		peer.on('signal', signal => {
			socket.emit('returning signal', { signal, callerID })
		})

		peer.signal(incomingSignal);
		return peer;
	}

	let setup = (p5, canvas) => {
		let canv = p5.createCanvas(800, 600).parent(canvas);
		let tempUsers = [];
		tempUsers.push({
			id: socket.id,
			room: roomID,
			x: 400,
			y: 100
		});

		setUsers(tempUsers);
	}

	let draw = (p5) => {
		p5.background("rgb(255, 255, 255)");

		let idx = users.findIndex((user) => user.id === socket.id)
		if (idx !== -1) {
			let tempUsers = users;

			if (p5.keyIsDown(87) || p5.keyIsDown(38)) {
				tempUsers[idx].y = tempUsers[idx].y - 2;
			}
			if (p5.keyIsDown(65) || p5.keyIsDown(37)) {
				tempUsers[idx].x = tempUsers[idx].x - 2;
			}
			if (p5.keyIsDown(83) || p5.keyIsDown(40)) {
				tempUsers[idx].y = tempUsers[idx].y + 2;
			}
			if (p5.keyIsDown(68) || p5.keyIsDown(39)) {
				tempUsers[idx].x = tempUsers[idx].x + 2;
			}	

			let data = {
				id: socket.id,
				room: roomID,
				x: tempUsers[idx].x,
				y: tempUsers[idx].y	
			}

			setUsers(tempUsers);
			socket.emit('send move', data);
		}

		for (let i = 0; i < users.length; i ++) {
			p5.circle(users[i].x, users[i].y, 16);
		}
	}

	const muteUnmute = (e) => {
		const newVideo = myVideo
		const enabled = newVideo.getAudioTracks()[0].enabled;
		if (enabled) {
			newVideo.getAudioTracks()[0].enabled = false;	
		} else {
			newVideo.getAudioTracks()[0].enabled = true;
		}
		setMyVideo(newVideo);
	}

	const cameraOnOff = (e) => {
		const newVideo = myVideo
		const enabled = newVideo.getVideoTracks()[0].enabled;
		if (enabled) {
			newVideo.getVideoTracks()[0].enabled = false;
		} else {
			newVideo.getVideoTracks()[0].enabled = true;
		}
		setMyVideo(newVideo)
	}

	const screenShare = (e) => {

	}

	const stopScreenShare = () => {

	}

	const proximity = (user, me) => {
		if ((user.x - me.x) * (user.x - me.x) + (user.y - me.y) * (user.y - me.y) <= 10000) {
			return true;
		} else {
			return false;
		}
	}

	const connectPeer = (user, me) => {


		let temp = connected;
		temp[user.id] = true;
		setConnected(temp);
		return;
	}

	const disconnectPeer = (user) => {


		let temp = connected;
		delete temp[user.id]
		setConnected(temp);
		return;
	}

  return (
		<div>
			{ joinedRoom ? (
				<div className="room">
					<div className="video-canvas">
						<div className="videobox">
							<div className="buttonbox">
								<button type="button" className="mute" onClick={ (e) => muteUnmute(e) }> Mute </button>
								<button type="button" className="camera" onClick={ (e) => cameraOnOff(e) }> Camera </button>
								<button type="button" className="screenshare" onClick={ (e) => screenShare(e) }> ScreenShare </button>
							</div>
							<Video peer={ myVideo } muted={ true } />
							<div className="videos">
								{ peers.map((peer, idx) => {
									return(
										<Video peer={ peer.peer } muted={ false }/>
									)
								})}
							</div>
						</div>
						<Sketch setup={ setup } draw={ draw } className="canvas" />
					</div>
					<Chat className="chat" socket={ socket } room={ roomID } name={ name } />
				</div>
				) : (
					<RoomSetup 
						setJoinedRoom={ () => setJoinedRoom(true) } 
						setMic={ preference => setMic(preference) } 
						setCam={ preference => setCam(preference) } 
						setName={ name => setName(name) }
					/>
				)
			}
		</div>
  );
};

export default Room;
